    @Test
    @Throws(JSONException::class)
    fun testTranslateSearchList() {
        //Arrange
        val jsonTranslator = JsonTranslator()
        val json =
            "[{\"id\":502428,\"name\":\"Bucaramanga\",\"region\":\"Santander\",\"country\":\"Colombia\",\"lat\":7.13,\"lon\":-73.13,\"url\":\"bucaramanga-santander-colombia\"},{\"id\":522388,\"name\":\"San Alonso\",\"region\":\"Santander\",\"country\":\"Colombia\",\"lat\":7.13,\"lon\":-73.12,\"url\":\"san-alonso-santander-colombia\"},{\"id\":518968,\"name\":\"Navas\",\"region\":\"Santander\",\"country\":\"Colombia\",\"lat\":7.13,\"lon\":-73.13,\"url\":\"navas-santander-colombia\"},{\"id\":509814,\"name\":\"Gaitan\",\"region\":\"Santander\",\"country\":\"Colombia\",\"lat\":7.13,\"lon\":-73.13,\"url\":\"gaitan-santander-colombia\"},{\"id\":524100,\"name\":\"Sotomayor\",\"region\":\"Santander\",\"country\":\"Colombia\",\"lat\":7.12,\"lon\":-73.11,\"url\":\"sotomayor-santander-colombia\"},{\"id\":515893,\"name\":\"Las Pinitas\",\"region\":\"Santander\",\"country\":\"Colombia\",\"lat\":7.12,\"lon\":-73.13,\"url\":\"las-pinitas-santander-colombia\"},{\"id\":519079,\"name\":\"Norte\",\"region\":\"Santander\",\"country\":\"Colombia\",\"lat\":7.15,\"lon\":-73.13,\"url\":\"norte-santander-colombia\"},{\"id\":504811,\"name\":\"Chitota\",\"region\":\"Santander\",\"country\":\"Colombia\",\"lat\":7.15,\"lon\":-73.12,\"url\":\"chitota-santander-colombia\"},{\"id\":520074,\"name\":\"Pegadero\",\"region\":\"Santander\",\"country\":\"Colombia\",\"lat\":7.15,\"lon\":-73.14,\"url\":\"pegadero-santander-colombia\"},{\"id\":502585,\"name\":\"Cabecera Del Llano\",\"region\":\"Santander\",\"country\":\"Colombia\",\"lat\":7.11,\"lon\":-73.11,\"url\":\"cabecera-del-llano-santander-colombia\"}]"

        //Act
        val result = jsonTranslator.translateSearchList(json)

        //Assert
        Assert.assertEquals("Colombia", result.searchResponseDataList[0].country)
        Assert.assertEquals("Bucaramanga", result.searchResponseDataList[0].name)
        Assert.assertEquals("Colombia", result.searchResponseDataList[1].country)
        Assert.assertEquals("San Alonso", result.searchResponseDataList[1].name)
        Assert.assertEquals("Colombia", result.searchResponseDataList[2].country)
        Assert.assertEquals("Navas", result.searchResponseDataList[2].name)
        Assert.assertEquals("Colombia", result.searchResponseDataList[3].country)
        Assert.assertEquals("Gaitan", result.searchResponseDataList[3].name)
        Assert.assertEquals("Colombia", result.searchResponseDataList[4].country)
        Assert.assertEquals("Sotomayor", result.searchResponseDataList[4].name)
        Assert.assertEquals("Colombia", result.searchResponseDataList[5].country)
        Assert.assertEquals("Las Pinitas", result.searchResponseDataList[5].name)
        Assert.assertEquals("Colombia", result.searchResponseDataList[6].country)
        Assert.assertEquals("Norte", result.searchResponseDataList[6].name)
        Assert.assertEquals("Colombia", result.searchResponseDataList[7].country)
        Assert.assertEquals("Chitota", result.searchResponseDataList[7].name)
        Assert.assertEquals("Colombia", result.searchResponseDataList[8].country)
        Assert.assertEquals("Pegadero", result.searchResponseDataList[8].name)
        Assert.assertEquals("Colombia", result.searchResponseDataList[9].country)
        Assert.assertEquals("Cabecera Del Llano", result.searchResponseDataList[9].name)
    }

